', '// handle configuration tasks (read/write to storage)
', 'class Configuration
', '{
', '    public static async Task<bool> Exists()
', '    {
', '        return await AzureStorage.Exists();
', '    }
', '
', '    // save configuration to the storage (used after get-config request)
', '    public static async Task<bool> Save(byte[] responseContent)
', '    {
', '        await AzureStorage.RestUploadBlob(responseContent);
', '        Globals._log.LogInformation($"Configuration.Save - saved {responseContent.Count()} bytes to configuration blob");
', '
', '        return true;
', '    }
', '
', '    // read configuration from storage and return it as file content
', '    public static async Task<ByteArrayContent> GetContent()
', '    {
', '        Globals._log.LogInformation($"Configuration.GetContent - entry");
', '
', '        // try to get configuration file from storage
', '        byte[] blobContent = await AzureStorage.RestDownloadBlob();
', '        if (null == blobContent || blobContent.Length == 0)
', '        {
', '            Globals._log.LogError($"Configuration.GetContent - failed to read configuration blob from storage");
', '
', '            return null;
', '        }
', '
', '        Globals._log.LogInformation($"Configuration.GetContent - total of {blobContent.Length} bytes read from storage");
', '
', '        return new ByteArrayContent(blobContent);
', '    }
', '};
', '
', '// handle reading and writing storage''s blob content
', '// currently, this class supports handling a single blob only
', '/* 
', 'in order to read/write storage blobs using REST API I''m following the example in this link: 
', 'https://stackoverflow.com/questions/48202964/azure-blob-storage-rest-call-from-c-sharp
', 'in general, the idea in this implementation is to acquire an access key from the storage and use 
', 'it as a key to encypt and generate an acces token (see ''AuthorizationHeader'' below). the access 
', 'token is a hash performed on a string that is a sort of permission request (string to sign). 
', 'for more details you can check the following links:
', 'https://docs.microsoft.com/en-us/rest/api/storageservices/constructing-a-service-sas
', 'https://docs.microsoft.com/en-us/rest/api/storageservices/service-sas-examples 
', 'and a lot more when searching for "REST API storage blob"
', '*/ 
', 'class AzureStorage
', '{
', '    static string  _accountName = "";
', '    static string  _containerName = "";
', '    static string  _blobName = "";
', '	static string  _blobServiceEp = "";
', '    static string  _blobType = "BlockBlob";
', '    static dynamic _accessKeys = null;
', '    static byte[]  _blobContent;
', '    static string  _version = "2017-04-17";
', '
', '    public static void Init(string accountName, string containerName, string blobName, string blobServiceEp)
', '    {
', '        _accountName = accountName;
', '        _containerName = containerName;
', '        _blobName = blobName;
', '		_blobServiceEp = blobServiceEp;
', '        _accessKeys = null; // i want to retrieve the access keys once per apply/first-apply
', '        _blobContent = new byte[0]; // enable content reuse (i need to copy the content after each put-config)
', '    }
', '
', '    // build the REST API to get a list of storage access keys for a specific account
', '    static string GenerateListKeysUrl()
', '    {
', '        string subscriptionId = Globals._deployParams?.Azure.SubscriptionId;
', '        string resourceGroup = Globals._deployParams?.Azure.ResourceGroupName;
', '        string apiVersion = "2018-07-01";
', '
', '        return $"https://management.azure.com/subscriptions/{subscriptionId}/resourceGroups/{resourceGroup}/providers/Microsoft.Storage/storageAccounts/{_accountName}/listKeys?api-version={apiVersion}";
', '    }
', '
', '    // retrieve the storage access keys (2 keys)
', '    static async Task<bool> GetAccessKeys()
', '    {
', '        Globals._log.LogInformation("AzureStorage.GetAccessKeys - entry");
', '        if (null != _accessKeys)
', '        {
', '            Globals._log.LogInformation("AzureStorage.GetAccessKeys - keys already retrieved, return them");
', '            return true;
', '        }
', '
', '        // generate the appropriate URL
', '        string queryUrl = GenerateListKeysUrl();
', '
', '        // send GET request to ARM to retrieve NAT rules
', '        HttpResponseMessage response = await AzureInterface.PostHttpResponse(queryUrl);
', '        if (null == response || response.StatusCode != HttpStatusCode.OK)
', '        {
', '            Globals._log.LogError($"AzureStorage.GetAccessKeys - failed to retrieve access keys. Status: [{response?.StatusCode}]");
', '            return false;
', '        }
', '
', '        string responseContent = await response.Content.ReadAsStringAsync();
', '
', '        Globals._log.LogInformation($"AzureStorage.GetAccessKeys - response: {responseContent}");
', '
', '        _accessKeys = JsonConvert.DeserializeObject(responseContent);
', '
', '        return true;
', '    }
', '
', '    static HttpWebRequest CreateBlobRequest(string method, string time, int contentLength = 0)
', '    {
', '        string requestUri = $"{_blobServiceEp}{_containerName}/{_blobName}";
', '
', '        HttpWebRequest request = (HttpWebRequest)WebRequest.Create(requestUri);
', '
', '        request.Method = method;
', '        if (contentLength > 0)
', '        {
', '            request.ContentType = "text/plain; charset=UTF-8";
', '            request.ContentLength = contentLength;
', '        }
', '
', '        request.Headers.Add("x-ms-version", _version);
', '        request.Headers.Add("x-ms-date", time);
', '        request.Headers.Add("x-ms-blob-type", _blobType);
', '
', '        return request;
', '    }
', '
', '    // generate the authorization token and header
', '    static string AuthorizationHeader(string method, string now, HttpWebRequest request, string accessKey)
', '    {
', '        string headerResource = $"x-ms-blob-type:{_blobType}\nx-ms-date:{now}\nx-ms-version:{_version}";
', '        string urlResource = $"/{_accountName}/{_containerName}/{_blobName}";
', '        string contentLength = (method == "GET" || method == "HEAD") ? String.Empty : request.ContentLength.ToString();
', '        string stringToSign = $"{method}\n\n\n{contentLength}\n\n{request.ContentType}\n\n\n\n\n\n\n{headerResource}\n{urlResource}";
', '
', '        Globals._log.LogInformation($"AzureStorage.AuthorizationHeader - string to sign: {stringToSign}");
', '
', '        HMACSHA256 hmac = new HMACSHA256(Convert.FromBase64String(accessKey));
', '        string signature = Convert.ToBase64String(hmac.ComputeHash(Encoding.UTF8.GetBytes(stringToSign)));
', '
', '        Globals._log.LogInformation($"AzureStorage.AuthorizationHeader - signature: {signature}");
', '
', '        return $"SharedKey {_accountName}:{signature}";
', '    }
', '
', '    // upload a blob/file to the storage using REST API
', '    public static async Task<bool> RestUploadBlob(byte[] content)
', '    {
', '        Globals._log.LogInformation("AzureStorage.RestUploadBlob - entry");
', '
', '        // save content to local _blobContent to be reused
', '        _blobContent = content;
', '
', '        // first get the access key
', '        if (!await GetAccessKeys())
', '        {
', '            Globals._log.LogError("AzureStorage.RestUploadBlob - failed to retrieve access keys");
', '
', '            return false;
', '        }
', '
', '        try
', '        {
', '            string method = "PUT";
', '            string now = DateTime.UtcNow.ToString("R");
', '
', '            int contentLength = content.Length;
', '            Globals._log.LogInformation($"AzureStorage.RestUploadBlob - total of {contentLength} bytes will be written to storage blob");
', '
', '            HttpWebRequest request = CreateBlobRequest(method, now, contentLength);
', '
', '            string token = AuthorizationHeader(method, now, request, _accessKeys.keys[0].value.ToString());
', '            Globals._log.LogInformation($"AzureStorage.RestUploadBlob - time: {now}, auth header: {token}");
', '
', '            request.Headers.Add("Authorization", token);
', '
', '            using (Stream requestStream = request.GetRequestStream())
', '            {
', '                requestStream.Write(content, 0, contentLength);
', '            }
', '
', '            using (HttpWebResponse response = (HttpWebResponse)request.GetResponse())
', '            {
', '                Globals._log.LogInformation($"AzureStorage.RestUploadBlob - done with status: {response.StatusCode.ToString()}");
', '            }
', '
', '            return true;
', '        }
', '        catch (Exception ex)
', '        {
', '            Globals._log.LogError($"AzureStorage.RestUploadBlob - an exception occured. Blob: {_blobName}\nException: {ex.ToString()}");
', '            return false;
', '        }
', '    }
', '
', '    public static async Task<byte[]> RestDownloadBlob()
', '    {
', '        // if content already downloaded, return it
', '        if (_blobContent.Length > 0)
', '        {
', '            Globals._log.LogInformation($"AzureStorage.RestDownloadBlob - blob already retrieved, return it");
', '            return _blobContent;
', '        }
', '
', '        Globals._log.LogInformation($"AzureStorage.RestDownloadBlob - first time retrieving blob");
', '
', '        // first get the access key
', '        if (!await GetAccessKeys())
', '        {
', '            Globals._log.LogError("AzureStorage.RestDownloadBlob - failed to retrieve access keys");
', '
', '            return null;
', '        }
', '
', '        try
', '        {
', '            Globals._log.LogInformation($"AzureStorage.RestDownloadBlob - got {_accessKeys.keys.Count} access keys");
', '
', '            string method = "GET";
', '            string now = DateTime.UtcNow.ToString("R");
', '
', '            Globals._log.LogInformation("AzureStorage.RestDownloadBlob - creating the request");
', '            HttpWebRequest request = CreateBlobRequest(method, now);
', '
', '            string token = AuthorizationHeader(method, now, request, _accessKeys.keys[0].value.ToString());
', '            Globals._log.LogInformation($"AzureStorage.RestDownloadBlob - time: {now}, token: {token}");
', '
', '            request.Headers.Add("Authorization", token);
', '
', '            using (HttpWebResponse response = (HttpWebResponse)request.GetResponse())
', '            {
', '                Globals._log.LogInformation($"AzureStorage.RestDownloadBlob - done with status: {response.StatusCode.ToString()}. Content length : {response.ContentLength}");
', '                using (var reader = new System.IO.BinaryReader(response.GetResponseStream()))
', '                {
', '                    _blobContent = reader.ReadBytes((int)response.ContentLength);
', '                    Globals._log.LogInformation($"AzureStorage.RestDownloadBlob - blob length: {_blobContent.Length}");
', '
', '                    return _blobContent;
', '                }
', '            }
', '        }
', '        catch (Exception ex)
', '        {
', '            Globals._log.LogError($"AzureStorage.RestDownloadBlob - an exception occured. Blob: {_blobName}\nException: {ex.ToString()}");
', '            return null;
', '        }
', '    }
', '
', '    // check existence of the configuration blob
', '    public static async Task<bool> Exists()
', '    {
', '        // this will either return immediately (blob already retrieved) or retrieve the
', '        // blob for the first time. the later will be usefull on next calls to RestDownloadBlob
', '        byte[] blobContent = await AzureStorage.RestDownloadBlob();
', '
', '        if (null == blobContent || blobContent.Length == 0)
', '        {
', '            return false;
', '        }
', '
', '        return true;
', '    }    
', '};
