', '// a collection of functions to handle SSL certificate injection
', 'class AlteonCertificate
', '{
', '    /// set license to Alteon (the one sending the first-apply)
', '    public static async Task<FuncResult> Set(string address)
', '    {
', '        // 1. set certificate key
', '        // 2. set certificate data
', '        Globals._log.LogInformation($"AlteonCertificate.Set - entry");
', '
', '        // read key and data from parameters file
', '        string id = Globals._deployParams?.Certificate?.Id;
', '        string passphrase = Globals._deployParams?.Certificate?.Passphrase;
', '        string key = Globals._deployParams?.Certificate?.Key;
', '        string data = Globals._deployParams?.Certificate?.Data;
', '        bool   isHttpService = Convert.ToBoolean(Globals._deployParams?.Certificate?.SlbHttpService);
', '
', '        if (string.IsNullOrEmpty(data) || string.IsNullOrEmpty(id) || string.IsNullOrEmpty(passphrase))
', '        {
', '            // certificate parameters not supplied, but since it''s optional, i''ll return success
', '            Globals._log.LogError("AlteonCertificate.Set - some certificate parameters are missing or empty. No certificate will be applied");
', '            return new FuncResult(true);
', '        }
', '
', '        if (string.IsNullOrEmpty(key))
', '        {
', '            // data actually contains both key and data - split them
', '            (key, data) = SplitData(data);
', '        }
', '
', '        if (string.IsNullOrEmpty(data) || string.IsNullOrEmpty(key))
', '        {
', '            // certificate key or data were not extracted
', '            Globals._log.LogError("AlteonCertificate.Set - failed to extract either certificate data or certificate key. Please verify the given certificate");
', '            return new FuncResult(false);
', '        }
', '
', '        Globals._log.LogInformation($"AlteonCertificate.Set - key size: {key.Length}, data size: {data.Length}");
', '
', '        // inject key
', '        string url = $"https://{address}/config/sslcertimport?renew=0&renewK=0&id={id}&type=key&src=txt&passphrase={passphrase}";
', '        FuncResult result = await Utils.PostRequest(url, 3, key);
', '        if (!result.status)
', '        {
', '            return result;
', '        }
', '
', '        // inject data
', '        url = $"https://{address}/config/sslcertimport?renew=0&renewK=0&id={id}&type=cert&src=txt";
', '        result = await Utils.PostRequest(url, 3, data);
', '        if (!result.status)
', '        {
', '            return result;
', '        }
', '
', '        // before apply, virt 1 needs to be enabled
', '        string virtId = "1";
', '
', '        // also before apply, virt 1 https service needs it''s certificate defined
', '        string rPort = Globals._deployParams?.Certificate?.HttpsRport;
', '        string policyName = Globals._deployParams?.Certificate?.Policy;
', '        if (string.IsNullOrEmpty(policyName) || string.IsNullOrEmpty(rPort))
', '        {
', '            Globals._log.LogInformation("AlteonCertificate.Set - notice: no policy or https port defined so VIRT configuration will not be complete");
', '            return result;
', '        }
', '
', '        int serviceIndex = 1;
', '        result = await AlteonInterface.SetVirtSslCertificateNPolicy(address, rPort, virtId, serviceIndex, id, policyName);
', '        if (!result.status)
', '        {
', '            return result;
', '        }
', '
', '        //Enable Real Scale Set
', '        result = await AlteonInterface.EnableRealSS(address);
', '        if (!result.status)
', '        {
', '            return result;
', '        }
', '        //Enable VAS Scale Set
', '        result = await AlteonInterface.EnableVasSS(address);
', '        if (!result.status)
', '        {
', '            return result;
', '        }
', '        //Assign AS script to service
', '        result = await AlteonInterface.AssignASToService(address, virtId);
', '        if (!result.status)
', '        {
', '            return result;
', '        }
', '
', '        //Enable VIRT
', '        result = await AlteonInterface.EnableVirt(address, virtId);
', '        if (!result.status)', '        {
', '            return result;
', '
', '        }
', '
','         result = await AlteonInterface.ApplyConfig(address);
', '
', '        return result;
', '    }
', '
', '    static (string, string) SplitData(string raw)
', '    {
', '        if (string.IsNullOrEmpty(raw))
', '        {
', '            Globals._log.LogError("AzureCertificate.SplitRaw - certificate is empty");
', '            return ("", "");
', '        }
', '        
', '        // data section starts with -----BEGIN CERTIFICATE----- line
', '        // and ends with -----END CERTIFICATE----- line
', '        string dataBegin = "-----BEGIN CERTIFICATE-----";
', '        string dataEnd = "-----END CERTIFICATE-----";
', '        int dataStartPos = raw.IndexOf(dataBegin);
', '        int dataEndPos = raw.IndexOf(dataEnd);
', '        if (dataStartPos < 0 || dataEndPos < 0)
', '        {
', '            Globals._log.LogError($"AzureCertificate.SplitRaw - can''t find certificate data section in {raw}");
', '            return ("", "");
', '        }
', '        string data = raw.Substring(dataStartPos, dataEndPos + dataEnd.Length - dataStartPos);
', '
', '        // key section starts with -----BEGIN RSA PRIVATE KEY----- line
', '        // and ends with -----END RSA PRIVATE KEY----- line
', '        string keyBegin = "-----BEGIN RSA PRIVATE KEY-----";
', '        string keyEnd = "-----END RSA PRIVATE KEY-----";
', '        int keyStartPos = raw.IndexOf(keyBegin);
', '        int keyEndPos = raw.IndexOf(keyEnd);
', '        if (keyStartPos < 0 || keyEndPos < 0)
', '        {
', '            Globals._log.LogError($"AzureCertificate.SplitRaw - can''t find certificate key section in {raw}");
', '            return ("", data);
', '        }
', '        string key = raw.Substring(keyStartPos, keyEndPos + keyEnd.Length - keyStartPos);
', '
', '        return (key, data);
', '    }
', '};
